// PHASE 6: Create A/B Test Variants
const items = $input.all();

return items.map(item => {
  const subjectLines = item.json.subject_line_variants || [];

  // Validation: Ensure we have at least 2 variants
  if (subjectLines.length < 2) {
    console.warn("Insufficient subject line variants. Falling back to single variant.");
    const fallbackSubject = subjectLines[0] || item.json.selected_subject_line || "Newsletter Update";
    return {
      json: {
        ...item.json,
        has_ab_test: false,
        fallback_reason: "Less than 2 subject line variants available",
        variant_a: {
          variant_id: "A",
          subject_line: fallbackSubject,
          test_type: "none",
          is_control: true
        },
        variant_b: null
      }
    };
  }

  // Use first two variants
  const variantA = subjectLines[0];
  const variantB = subjectLines[1];

  return {
    json: {
      ...item.json,
      has_ab_test: true,
      variant_a: {
        variant_id: "A",
        subject_line: variantA,
        test_type: "subject_line",
        is_control: true
      },
      variant_b: {
        variant_id: "B",
        subject_line: variantB,
        test_type: "subject_line",
        is_control: false
      },
      ab_test_config: {
        test_name: `Subject Line Test - ${item.json.segment_name || "Default"}`,
        test_start_date: new Date().toISOString(),
        split_percentage: 50,
        winner_criteria: "open_rate",
        min_sample_size: 100
      }
    }
  };
});

// PHASE 6: Assign Contacts to Variants (Deterministic)
const items = $input.all();

// Simple deterministic hash function
function hashString(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32bit integer
  }
  return Math.abs(hash);
}

return items.map(item => {
  const contacts = item.json.segment_contacts || [];
  const issueId = item.json.issue_id;
  const hasABTest = item.json.has_ab_test !== false; // Default true unless explicitly false

  // Handle empty contact list
  if (contacts.length === 0) {
    console.warn("No contacts available for variant assignment");
    return {
      json: {
        ...item.json,
        has_ab_test: false,
        contact_assignments: [],
        variant_a_count: 0,
        variant_b_count: 0,
        assignment_error: "No contacts to assign"
      }
    };
  }

  // If A/B test disabled, assign all to variant A
  if (!hasABTest) {
    const assignments = contacts.map(contact => ({
      contact_id: contact.id,
      email: contact.email,
      variant_id: "A",
      subject_line: item.json.variant_a.subject_line,
      assigned_at: new Date().toISOString(),
      first_name: contact.first_name,
      preferences: contact.preferences
    }));

    return {
      json: {
        ...item.json,
        contact_assignments: assignments,
        variant_a_count: assignments.length,
        variant_b_count: 0,
        split_ratio: "100/0"
      }
    };
  }

  // Deterministic 50/50 assignment using hash
  const assignments = contacts.map(contact => {
    const hashInput = `${contact.id}-${issueId}`;
    const hash = hashString(hashInput);
    const assignedVariant = hash % 2 === 0 ? "A" : "B";
    const variantConfig = assignedVariant === "A" ? item.json.variant_a : item.json.variant_b;

    return {
      contact_id: contact.id,
      email: contact.email,
      variant_id: assignedVariant,
      subject_line: variantConfig.subject_line,
      assigned_at: new Date().toISOString(),
      first_name: contact.first_name,
      preferences: contact.preferences
    };
  });

  const variantACount = assignments.filter(a => a.variant_id === "A").length;
  const variantBCount = assignments.filter(a => a.variant_id === "B").length;
  const totalCount = assignments.length;

  console.log(`Variant Assignment: A=${variantACount}, B=${variantBCount}, Total=${totalCount}`);

  return {
    json: {
      ...item.json,
      contact_assignments: assignments,
      variant_a_count: variantACount,
      variant_b_count: variantBCount,
      split_ratio: `${Math.round(variantACount/totalCount*100)}/${Math.round(variantBCount/totalCount*100)}`,
      assignment_stats: {
        total_contacts: totalCount,
        variant_a_contacts: variantACount,
        variant_b_contacts: variantBCount,
        assignment_method: "deterministic_hash"
      }
    }
  };
});

// Increment retry counter
const item = $input.first();
const retryCount = item.json.retry_count || 0;

if (retryCount >= 2) {
  // Max retries reached, flag for manual review
  return [{
    json: {
      ...item.json,
      status: 'validation_failed',
      requires_manual_review: true
    }
  }];
}

return [{
  json: {
    ...item.json,
    retry_count: retryCount + 1
  }
}];
